// Declare the namespace.
namespace CodePotent\UpdateManager;

// Prevent direct access.
if (!defined('ABSPATH')) {
	die();
}

// Gain access to the get_home_path() function.
require_once(ABSPATH.'wp-admin/includes/file.php');

// -----------------------------------------------------------------------------
// BASIC PLUGIN CONSTANTS
// -----------------------------------------------------------------------------
// Vendor prefix; ie, yourprefix or your-prefix
const VENDOR_PREFIX = 'codepotent';
// Plugin name in user-readable format.
const PLUGIN_NAME = 'Update Manager';
// Plugin version number.
const PLUGIN_VERSION = '1.0.1';
// Plugin directory name; ie, codepotent-plugin-name
define(__NAMESPACE__.'\PLUGIN_DIRNAME', substr(str_replace('\\', '/', dirname(__DIR__)), strrpos(str_replace(['\\', '/'], '/', dirname(__DIR__)), '/')+1));
// Plugin file name; ie, codepotent-plugin-name.php
const PLUGIN_FILENAME = PLUGIN_DIRNAME.'.php';
// Plugin slug, long; ie, codepotent-my-plugin-name.
const PLUGIN_SLUG = PLUGIN_DIRNAME;
// Plugin slug, short; ie, my-plugin-name
define(__NAMESPACE__.'\PLUGIN_SLUG_SHORT', str_replace(VENDOR_PREFIX.'-', '', PLUGIN_DIRNAME));
// Plugin file name; ie, codepotent-plugin-name/codepotent-plugin-name.php
const PLUGIN_IDENTIFIER = PLUGIN_DIRNAME.'/'.PLUGIN_FILENAME;
// Plugin prefix; for settings, form inputs, hooks; to avoid collisions.
define(__NAMESPACE__.'\PLUGIN_PREFIX', str_replace('-', '_', PLUGIN_DIRNAME));
// Admin mmenu icon.
const PLUGIN_MENU_ICON = null;
// Admin menu position.
const PLUGIN_MENU_POS = null;
// Admin menu position.
const PLUGIN_SETTINGS_VAR = PLUGIN_PREFIX.'_settings';
// Plugin text domain.
const PLUGIN_TEXT_DOMAIN = PLUGIN_DIRNAME;
// Plugin homebase.
const PLUGIN_HOME_URL = 'https://codepotent.com/classicpress/plugins';
// Plugin author name.
const PLUGIN_AUTHOR = 'Code Potent';
// Plugin author URL.
const PLUGIN_AUTHOR_URL = PLUGIN_HOME_URL;
//Plugin review URL.
const PLUGIN_REVIEW_URL = PLUGIN_HOME_URL.'/'.PLUGIN_SLUG.'#testimonials';
//Plugin documentation URL.
const PLUGIN_DOCS_URL = PLUGIN_HOME_URL.'/'.PLUGIN_SLUG.'#docs';
//Plugin support (forum? Slack?) URL.
const PLUGIN_SUPPORT_URL = PLUGIN_HOME_URL.'/'.PLUGIN_SLUG.'#docs';
// Plugin repo URL.
const PLUGIN_REPO_URL = 'https://github.com/'.VENDOR_PREFIX.'/'.PLUGIN_SLUG_SHORT;

// -----------------------------------------------------------------------------
// CPT, TAXONOMY, JSON
// -----------------------------------------------------------------------------
// Primary query variable for the JSON endpoints.
const ENDPOINT_VARIABLE = 'update';
// Custom post type for plugin update endpoints.
const CPT_FOR_PLUGIN_ENDPOINTS = 'plugin_endpoint';

